mapper.select:
select id, classroom_name, content, classroom_num, sort_no, create_time, create_by, update_time, update_by, version, del_flag
from t_school_classroom
where id = #{id}

mapper.update:
update t_school_classroom
classroom_name = #{classroomName}, content = #{content}, classroom_num = #{classroomNum}, create_time = #{createTime}, create_by = #{createBy}, update_by = #{updateBy}, version = #{version}, del_flag = #{delFlag}, update_time = sysdate()
where id = #{id}
