mapper.select:
SELECT *
FROM t_data_warning
WHERE
     del_flag = '0'


    AND device_name = #{deviceName}




    AND data_warning_type = #{dataWarningType}




    AND processing_status = #{processingStatus}




    AND create_time >= #{beginTime}




    AND create_time <= #{endTime}




    AND space_type = #{spaceType}




    AND  space_id = #{spaceId}




    AND id IN

        #{id}

SELECT data_warning_type, COUNT( 1 ) as dataWarningValue FROM t_data_warning WHERE del_flag = '0'
AND space_id = #{spaceId}
GROUP BY data_warning_type
